import React, { useState, useEffect } from "react";
import { Keycloak } from "keycloak-js";

const useAuth = () => {
  const [isLogin, setLogin] = useState(false);

  useEffect(() => {
    const keycloak = new Keycloak({
      url: import.meta.env.KEYCLOACK_URL, // URL Keycloak сервера
      realm: import.meta.env.KEYCLOACK_REALM, // Название твоего Realm
      clientId: import.meta.env.KEYCLOAK_USER_ID, // ID клиента
    });
    console.log(keycloak);
  }, []);

  return isLogin;
};

export default useAuth;
